mapper.select:
SELECT posts_data.*, (SELECT user_nickname FROM user_data WHERE user_id = posts_data.post_user_id) as user_nickname, (SELECT count(*) FROM post_comment WHERE comment_post_id = posts_data.post_id) as post_comment_count, (SELECT count(*) FROM post_like WHERE like_post_id = posts_data.post_id) as post_like_count, post_like.like_id as post_like_id FROM posts_data LEFT JOIN post_like ON posts_data.post_id = post_like.like_post_id AND post_like.like_user_id = #{userId}
posts_data.post_title like concat('%', #{keyword}, '%') OR posts_data.post_content like concat('%', #{keyword}, '%')
ORDER BY post_creation_date desc
